import React, { useState } from 'react';

interface UserPreferences {
  dailyCalorieGoal: number;
  weeklyWorkoutGoal: number;
  notificationsEnabled: boolean;
  theme: 'light' | 'dark';
}

const ProfileSettings: React.FC = () => {
  const [preferences, setPreferences] = useState<UserPreferences>({
    dailyCalorieGoal: 500,
    weeklyWorkoutGoal: 5,
    notificationsEnabled: true,
    theme: 'light'
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Save preferences to local storage
    localStorage.setItem('userPreferences', JSON.stringify(preferences));
  };

  return (
    <div className="card transform hover:shadow-xl transition-all duration-300">
      <h2 className="text-xl font-bold mb-6">Profile Settings</h2>
      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="space-y-4">
          <div className="transform transition-all duration-300 hover:translate-x-1">
            <label className="block text-sm font-medium text-gray-700">
              Daily Calorie Goal
            </label>
            <input
              type="number"
              value={preferences.dailyCalorieGoal}
              onChange={(e) => setPreferences({
                ...preferences,
                dailyCalorieGoal: Number(e.target.value)
              })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm
                focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50
                transition-all duration-300"
              min="0"
            />
          </div>

          <div className="transform transition-all duration-300 hover:translate-x-1">
            <label className="block text-sm font-medium text-gray-700">
              Weekly Workout Goal
            </label>
            <input
              type="number"
              value={preferences.weeklyWorkoutGoal}
              onChange={(e) => setPreferences({
                ...preferences,
                weeklyWorkoutGoal: Number(e.target.value)
              })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm
                focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50
                transition-all duration-300"
              min="1"
              max="7"
            />
          </div>

          <div className="transform transition-all duration-300 hover:translate-x-1">
            <label className="flex items-center space-x-3">
              <input
                type="checkbox"
                checked={preferences.notificationsEnabled}
                onChange={(e) => setPreferences({
                  ...preferences,
                  notificationsEnabled: e.target.checked
                })}
                className="rounded border-gray-300 text-primary 
                  focus:ring-primary focus:ring-opacity-50
                  transition-all duration-300"
              />
              <span className="text-sm font-medium text-gray-700">
                Enable Notifications
              </span>
            </label>
          </div>

          <div className="transform transition-all duration-300 hover:translate-x-1">
            <label className="block text-sm font-medium text-gray-700">
              Theme
            </label>
            <select
              value={preferences.theme}
              onChange={(e) => setPreferences({
                ...preferences,
                theme: e.target.value as 'light' | 'dark'
              })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm
                focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50
                transition-all duration-300"
            >
              <option value="light">Light</option>
              <option value="dark">Dark</option>
            </select>
          </div>
        </div>

        <button
          type="submit"
          className="w-full px-4 py-2 bg-primary text-white rounded-lg
            transform transition-all duration-300 ease-in-out
            hover:bg-primary/90 hover:scale-105 hover:shadow-lg
            active:scale-95"
        >
          Save Preferences
        </button>
      </form>
    </div>
  );
};

export default ProfileSettings;
